func BenchmarkJI_TestStringAppend_Parallel(b *testing.B) {
	b.RunParallel(func(pb *testing.PB) {
		for pb.Next() {
			s := ""
			for j := 0; j < count; j++ {
				s = s + str
			}
		}
	})
}
